import { Component } from '@angular/core';
import { AppComponent } from 'src/app/app.component';
import { BluetoothService } from 'src/app/services/bluetooth.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss'],
})
export class HomeComponent {
  platform: string = '';
  devices: any[] = [];
  connectedDevices: any[] = [];
  keepReloading = true;
  constructor(
    private bluetoothService: BluetoothService,
    private appComponent: AppComponent
  ) {
    this.platform = this.bluetoothService.getOsAsObservable();
    this.pool();
  }

  ngOnDestroy() {
    this.keepReloading = false;
  }

  pool() {
    let aux_this = this;
    setTimeout(() => {
      if (aux_this.keepReloading) {
        aux_this.findDevices();
        aux_this.pool();
      }
    }, 3000);
  }

  async getResponse() {
    this.getConnectedDevices();
    console.log(this.connectedDevices);
  }

  async findDevices() {
    this.devices = await this.bluetoothService.getBluetoothDevices();
    let connected = this.getConnectedDevices();
    this.devices.map((device: any) => {
      for (let i = 0; i < connected.length; i++) {
        if (connected[i].address == device.address) {
          device['serial'] = true;
        }
      }
      device['serial'] = false;
      return device;
    });
    console.log(this.devices);
  }

  async connectToDevice(id: number) {
    this.keepReloading = false;
    this.devices[id].loading = true;
    this.bluetoothService
      .connectToDevice(this.devices[id].address)
      .then(() => {
        this.appComponent.electron.ipcRenderer.sendSync('add-serial-device', {
          address: this.devices[id].address,
          name: this.devices[id].name,
        });
        this.devices[id].loading = false;
        this.keepReloading = true;
        this.pool();
      })
      .catch(() => {
        this.devices[id].loading = false;
      });
  }

  getConnectedDevices() {
    return this.appComponent.electron.ipcRenderer.sendSync(
      'get-serial-devices'
    );
  }
}
<div class="home-container">
  <div class="top-container">
    <button type="button" class="btn btn-light" (click)="findDevices()">
      <img src="assets/images/refresh-cw.svg" />
    </button>
  </div>
  <div class="table-container">
    <table>
      <tr>
        <th>Number</th>
        <th>Name</th>
        <th>Address</th>
        <th></th>
      </tr>
      <tr *ngFor="let device of devices; let i = index">
        <td>{{ i + 1 }}</td>
        <td>{{ device.name }}</td>
        <td>{{ device.address }}</td>
        <td>
          <button
            type="button"
            class="btn btn-primary"
            [disabled]="device.loading"
            *ngIf="!device.connected && !device.serial"
            (click)="connectToDevice(i)"
          >
            Connect
          </button>
          <div *ngIf="device.loading" class="spinner-border" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
        </td>
      </tr>
    </table>
  </div>
  <div class="bottom-container"></div>
</div>
